package Basics;

public class Typecasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//implicit casting
		int a=10;
		double b=a;
		System.out.println(b);
		
		//explicit conversion
		double c=12.653;
		int f=(int)c;
		
		System.out.println(f);
		
	}

}
